{let e=$("#add-friend-btn>a"),n=$("#remove-friend-btn>a"),r=function(){e.click((function(n){console.log("click event function called"),n.preventDefault(),$.ajax({type:"get",url:e.attr("href")}).done((function(e){let n=newFriendDom(e.data.friendship);$("#user-friendships-list-container").prepend(n),$("#friendship-btn-container").prepend(`<button id="remove-friend-btn">\n                <a href="/friendships/remove/?id=${e.data.friendship.to_user.id}">Remove</a>\n                </button>`),$("#add-friend-btn").remove()})).fail((function(e){console.log("error in completing the request :",e)}))}))};newFriendDom=function(e){return $(`<p>\n    <a href="/users/profile/${e.to_user.id}"> ${e.to_user.name} </a>\n    </p>`)};let t=function(){n.click((function(e){e.preventDefault(),$.ajax({type:"get",url:n.attr("href")}).done((function(e){$(`#friend-${e.data.id}`).remove(),$("#friendship-btn-container").prepend(`<button id="add-friend-btn">\n             <a href="/friendships/add/?id=${e.data.removedId}">ADD</a>\n            </button>`),$("#remove-friend-btn").remove()})).fail((function(e){console.log("Error in removing friend :",e)}))}))};r(),t()}